import React, {useState, useRef} from 'react'
import CreateNewPost from './CreateNewPost'

const DisplayAllPosts = () => {
  const [title, setTitle] = useState("")
  const [content, setContent] = useState("")
  const [allPost, setAllPost] = useState([])
  const [isCreateNewPost, setIsCreateNewPost] = useState(false)
  const getTitle = useRef()
  const getContent = useRef()

  const savePostTitleToSet = event => {
    setTitle(event.target.value)
    console.log(title)
  }

  const savePostContentToSet = event => {
    setContent(event.target.value)
    console.log(content)
  }

  const toggleCreateNewPost = () => {
    setIsCreateNewPost(!isCreateNewPost)
  }

  const savePost = (event) => {
    event.preventDefault()
    const id = Date.now()
    setAllPost([...allPost, {title, content, id}])
    console.log(allPost)
    getTitle.current.value="";
    getContent.current.value=""
    toggleCreateNewPost()
  }

  if(isCreateNewPost) {
    return (
      <>
      <CreateNewPost
      savePostTitleToSet = {savePostTitleToSet}
      savePostContentToSet = {savePostContentToSet} 
      getTitle={getTitle}
      getContent={getContent}
      savePost={savePost}
      />
      </>
    )
  }
  return (
    <>
    <h2>All Posts</h2>
    {!allPost.length ? (
      <div>
        <h3>nothing</h3>
      </div>
    ) : (
      allPost.map(eachPost => {
        return (
          <Post 
            id={eachPost}
            key={eachPost.id}
            title={eachPost.title}
            content={eachPost.content}
          />
        )
      })
    )}

    <br />
    <br />
    <button onClick={toggleCreateNewPost}>Create New</button>

    </>
  )
}

export default DisplayAllPosts
