import React, { useState, useReducer} from 'react';
import './Form.css';

//pourquoi formReducer en dehors du composant? 
const formReducer = (state, event) => {
  return {
    ...state, 
    [event.target.name]: event.target.value
  }
}
const Form = () => {
  const [formData, setFormData] = useReducer(formReducer, {})
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = event => {
    event.preventDefault();
    setSubmitting(true);
    console.log(event.target.name.value);

    //TODO setTimeout pour simuler l'API pour le moment 
    setTimeout(() => {
      setSubmitting(false);
    }, 3000)
  }

/*   const handleChange = event => {
    setFormData({
      name: event.target.name, 
      value: event.target.value

    });
  } */

  return (
    <div className="wrapperForm">
      <h1>Composant Form</h1>
      {submitting && 
        <div>Submitting form...
          {/* <ul>
            {Object.entries(formData).map(([name, event.target.name]) => 
            (
<li key={name}>{name}: {event.target.name.toString()}</li>
            ))}
          </ul> */}
      </div>}
      <form onSubmit={handleSubmit}>
        <fieldset>
          <label>
            <p>Nom</p>
            <input name="name" onChange={setFormData}/>
          </label>
        </fieldset>
        <button type="submit">Envoyer</button>
      </form>
    </div>
  )
}

export default Form
